#  Largest 5 digit number in a series
#  7 kyu
#  https://www.codewars.com/kata/51675d17e0c1bed195000001
#
#               w r i t t e n    b y
#   oooo        https://github.com/lcsm29        .oooo.    .ooooo.
#   `888                                       .dP""Y88b  888' `Y88.
#    888   .ooooo.   .oooo.o ooo. .oo.  .oo.         ]8P' 888    888
#    888  d88' `"Y8 d88(  "8 `888P"Y88bP"Y88b      .d8P'   `Vbood888
#    888  888       `"Y88b.   888   888   888    .dP'           888'
#    888  888   .o8 o.  )88b  888   888   888  .oP     .o     .88P'
#   o888o `Y8bod8P' 8""888P' o888o o888o o888o 8888888888   .oP'
def solution(digits: str) -> int:
    return int(max([digits[i:i + 5] for i in range(len(digits) - 4)]))


if __name__ == '__main__':
    basic_tests = [
        ["solution", 
        '', 
        99_890],
        ["solution", '1234567898765', 98_765]
    ]
    for test in basic_tests:
        fn_name, digits, expected = test
        result = globals()[fn_name](digits)
        print(f'{fn_name}({digits}) returns {result}'
              f'{f", expected: {expected}" if result != expected else ""}')


